import '../styling/Filters.css'
import React, { useState } from 'react';

const Filters = () => {
    const [selectedOption, setSelectedOption] = useState('');
    const [isOpen, setIsOpen] = useState(false);
    const dropdownRef = useRef(null);

    const handleOptionChange = (option) => {
        setSelectedOption(option === selectedOption ? '' : option);
    };

    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    };

    const handleMouseEnter = () => {
        setIsOpen(true);
    };

    const handleMouseLeave = (event) => {
        if (dropdownRef.current && !dropdownRef.current.contains(event.relatedTarget)) {
            setIsOpen(false);
        }
    };

    const options = ['Media', 'Government', 'Academics', 'Other'];

    return (
        <div
            className="dropdown-container"
            ref={dropdownRef}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
        >
            <div className="dropdown-header">Select Account Types</div>
            {isOpen && (
                <div className="dropdown-content">
                    {options.map((option) => (
                        <div
                            key={option}
                            className={`dropdown-option ${selectedOption === option ? 'selected' : ''}`}
                            onClick={() => handleOptionChange(option)}
                        >
                            {selectedOption === option && <span className="checkbox">&#10003;</span>}
                            <span>{option}</span>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};
export default Filters;
