import '../styling/Filters.css'
import React, { useState } from 'react';

const Filters = () => {
    const [selectedOption, setSelectedOption] = useState('');
    const [isOpen, setIsOpen] = useState(false);

    const handleOptionChange = (option) => {
        setSelectedOption(option === selectedOption ? '' : option);
    };

    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    };

    const options = ['XS', 'S', 'M', 'L', 'XL', 'XXL', '3XL'];

    return (
        <div className="dropdown-container">
            <div
                className="dropdown-header"
                onMouseEnter={toggleDropdown}
                onMouseLeave={toggleDropdown}
            >
                Selected Filters: Menswear
            </div>
            {isOpen && (
                <div className="dropdown-content">
                    {options.map((option) => (
                        <div
                            key={option}
                            className={`dropdown-option ${selectedOption === option ? 'selected' : ''}`}
                            onClick={() => handleOptionChange(option)}
                        >
                            {selectedOption === option && <span className="checkbox">&#10003;</span>}
                            <span>{option}</span>
                            <span className="option-count">48</span>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};
export default Filters;
