import '../styling/Filters.css'
import React, { useState } from 'react';

const Filters = () => {
    const [selectedOption, setSelectedOption] = useState('');

    const handleOptionChange = (option) => {
        setSelectedOption(option);
    };

    const options = ['XS', 'S', 'M', 'L', 'XL', 'XXL', '3XL'];

    return (
        <div className="dropdown-container">
            <div className="dropdown-header">Selected Filters: Menswear</div>
            <div className="dropdown-content">
                {options.map((option) => (
                    <div
                        key={option}
                        className={`dropdown-option ${selectedOption === option ? 'selected' : ''}`}
                        onClick={() => handleOptionChange(option)}
                    >
                        {selectedOption === option && <span className="checkbox">&#10003;</span>}
                        <span>{option}</span>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Filters;
