import './App.css';
import Header from "./components/Header";
import Filters from "./components/Filters";
import TimeFrameSelector from "./components/TimeFrameSelector";
import Twitter from "./components/Twitter";
import {useState} from "react";

function App() {

    const Account_Type_Options = ["Academic", "Government", "Media", "Other", "Tourism"]
    const County_Options = ["Pasco", "Pinellas", "Hillsborough", "Manatee", "Sarasota"]
    const Word_Cloud_Options = ["Geo Tags", "Non-Geo Tags"]

    const [selectedFilters, setSelectedFilters] = useState({
        timeFrame: [],
        accountType: [],
        county: [],
        wordCloud: [],
    });
    const handleFilterChange = (filterType, selectedOptions) => {
        setSelectedFilters(prevState => ({
            ...prevState,
            [filterType]: selectedOptions
        }));
    };

    const [selectedTimeFrame, setSelectedTimeFrame] = useState({
        startDate: '2018-06-30',
        endDate: new Date().toISOString().slice(0, 10)
    });

    const handleTimeFrameChange = (startDate, endDate) => {
        setSelectedTimeFrame({
            startDate,
            endDate
        });
        handleFilterChange('timeFrame', selectedTimeFrame)
    };


    return (
    <div className="App">
      <header className="App-header">
        <Header />
      </header>
      <div className="Filters_Bar">
          <TimeFrameSelector onTimeFrameChange={handleTimeFrameChange} />
          <Filters options={Account_Type_Options} Title={"ACCOUNT TYPE"} onChange={(selectedOptions) =>
              handleFilterChange('accountType', selectedOptions)
          } />
          <Filters options={County_Options} Title={"COUNTY"} onChange={(selectedOptions) => handleFilterChange('county', selectedOptions)} />
          <Filters options={Word_Cloud_Options} Title={"WORD CLOUD"} onChange={(selectedOptions) => handleFilterChange('wordCloud', selectedOptions)} />
      </div>
      <div className="Body">

          <div className="Twitter_container">
              Item 1
              <Twitter selectedFilters={selectedFilters}/>
          </div>
          <div className="Word_Cloud_container">
              Item 2
          </div>
          <div className="Map_container">
              Item 3
          </div>

      </div>
    </div>
  );
}

export default App;
