import React, { useEffect, useState, useMemo, useCallback } from "react";
import ReactWordcloud from "react-wordcloud";
import debounce from "lodash/debounce";
import 'tippy.js/dist/tippy.css';
import {getStopWords} from "./Locations";


const WordCloud = ({ cloud_type, tweets, onWordCloudClick}) => {


    const [words, setWords] = useState([{
        text: "UNDER CONSTRUCTION",
        value: 20
    }]);
    const [loading, setLoading] = useState(false);

    function extractHashtags(sentence) {
        const result = [];
        for (const word of sentence) {
            if (word[0] === '#' && !result.includes(word)) {
                result.push(word);
            }
        }
        return result;
    }

    function filterHashtags(tokenizedSentence) {
        const result = [];
        for (const w of tokenizedSentence) {
            const hashtag = w;
            const pureWord = hashtag.replace(/#/g, "").toLowerCase();
            let approved = true;
            const stopWords = getStopWords(true);

            for (const word of stopWords) {
                if (pureWord.includes(word.toLowerCase())) {
                    approved = false;
                    break;
                }
            }
            if (approved) {
                result.push(pureWord);
            }
        }
        return result;
    }

    function geoTagHarvester(listOfHashtags) {
        const pureSet = getCombinedLocations("combined")
        const paddedSet = categoryAdder("combined");

        for (const word of listOfHashtags) {
            let caught = false;

            for (const word2 of pureSet) {
                if (word.includes(word2.toLowerCase()) || word2.toLowerCase().includes(word)) {
                    this.geoTagDict[word] = (this.geoTagDict[word] || 0) + 1;
                    caught = true;
                    break;
                }
            }

            if (!caught) {
                for (const word2 of paddedSet) {
                    if (word.includes(word2.toLowerCase())) {
                        this.geoTagDict[word] = (this.geoTagDict[word] || 0) + 1;
                        caught = true;
                        break;
                    }
                }
            }

            if (!caught) {
                this.nonGeoHashtagsDict[word] = (this.nonGeoHashtagsDict[word] || 0) + 1;
            }
        }
    }


    const fetchTerms = () => {
        const justTextCol = [];
        for (const line of tweets) {
            // console.log(line['text'])
            let modifiedLine = line['text'].replace(/\bhttps\w*\b.*/, '');
            modifiedLine = modifiedLine.replace(/\bhttp\w*\b.*/, '');
            justTextCol.push(modifiedLine);
        }
        // console.log(justTextCol)
        const tokenizedTextColumn = [];
        for (const line of justTextCol) {
            const myPunct = ['!', '"', '$', '%', '&', "'", '(', ')', '*', '+', ',', '.', '/', ':', ';', '<', '=', '>', '?', '@', '[', '\\', ']', '^', '_', '-', '`', '{', '|', '}', '~', '»', '«', '“', '”'];

            const punctPattern = new RegExp("[" + myPunct.map(punct => "\\" + punct).join("") + "]", "g");
            let modifiedLine = line.replace(punctPattern, "");
            modifiedLine = modifiedLine.replace(/[^\w\s\']/g, "");
            modifiedLine = modifiedLine.replace(/\s+/g, " ");
            modifiedLine = modifiedLine.replace(/(https|www).*com/g, "");
            modifiedLine = modifiedLine.trim();
            const tokenizedText = modifiedLine.split(" ");

            if (!tokenizedTextColumn.some(existingText => JSON.stringify(existingText) === JSON.stringify(tokenizedText))) {
                const hashtags = extractHashtags(tokenizedText);
                const filteredTags = filterHashtags(hashtags);
                geoTagHarvester(filteredTags);
                tokenizedTextColumn.push(tokenizedText);
            }
        }

        console.log(tokenizedTextColumn)

        let result = []
        if (cloud_type.includes('Non-Geo')) {
            const nonGeo = nonGeoHashtagsDict;
            result = Object.entries(nonGeo).map(([key, value]) => ({ "text": key, "value": value }));
        } else if (cloud_type.includes('Geo')) {
            const geo = geoTagDict;
            result = Object.entries(geo).map(([key, value]) => ({ "text": key, "value": value }));
        } else if (cloud_type.includes('Single')) {
            result = getSingleTermWords();
        }

        return result

    }


    useEffect(() => {
        if (tweets.length > 0) {
            setLoading(true)
            fetchTerms();
        }

        if (tweets.length === 0) {
            setWords([{
                text: "LOADING",
                value: 20
            }])
        }
    }, [fetchTerms, tweets]);


    // const fetchTerms = useCallback(
    //     debounce(() => {
    //         setLoading(true);
    //         fetch("/get_terms", {
    //             method: "POST",
    //             headers: {
    //                 "Content-Type": "application/json",
    //             },
    //             body: JSON.stringify([cloud_type, tweets]),
    //         })
    //             .then((response) => response.json())
    //             .then((data) => {
    //                 setWords(data.value1);
    //                 setLoading(false);
    //             })
    //             .catch((error) => {
    //                 console.error(error);
    //                 setLoading(false);
    //             });
    //     }, 500),
    //     [cloud_type, tweets]
    // );

    // useEffect(() => {
    //     if (tweets.length > 0) {
    //         setLoading(true)
    //         fetchTerms();
    //     }
    //
    //     if (tweets.length === 0) {
    //         setWords([{
    //             text: "LOADING",
    //             value: 20
    //         }])
    //     }
    // }, [fetchTerms, tweets]);
    //
    // useEffect(() => {
    //     if (words.length === 0) {
    //         setWords([{
    //             text: "NO HASHTAGS AVAILABLE",
    //             value: 20
    //         }])
    //     }
    // }, [words]);

    const options = useMemo(
        () => ({
            rotations: 1,
            rotationAngles: [0],
            fontSizes: [15, 60],
            colors: ["#1f77b4", "#ff7f0e", "#2ca02c", "#d62728", "#9467bd", "#8c564b"],
            enableTooltip: true,
            deterministic: true,
            fontFamily: "impact",
            fontStyle: "normal",
            fontWeight: "normal",
            padding: 1,
            scale: "sqrt",
            spiral: "archimedean",
            transitionDuration: 1000,
        }),
        []
    );

    const callbacks = useMemo(
        () => ({
            onWordClick: (word) => onWordCloudClick('#'+word.text)
        }),
        []
    );

    const size = useMemo(() => [500, 600], []);

    return (
        <div className="word-cloud-container">
            <div className="loading">{loading && <p>Loading...</p>}</div>
            <div className="word-cloud">
                <ReactWordcloud  words={words} options={options} size={size} padding={0} callbacks={callbacks}/>
            </div>
        </div>
    );
};

export default WordCloud;
